#!/usr/bin/env node

// Voyga

"use strict";

const perfume = require(".");
const string = process.argv[2];

if (string !== undefined) {

	console.log(perfume(string));

} else {

	process.stdin.resume();
	process.stdin.setEncoding("utf8");

	process.stdin.on("data", (data) => {

		process.stdout.write(perfume(data));

	});

}