// Voyga

var assert = require("assert"),
	perfume = require("./../.");

describe("perfume", () => {

	it("should style a string", () => {

		assert.equal(perfume("Hello (World):red"), "Hello \u001b[31mWorld\u001b[39m");

	});

	it("should style a template string", () => {

		const name = "Tiga";
		assert.equal(perfume(`Hello (${name}):red`), "Hello \u001b[31mTiga\u001b[39m");

	});

	it("should apply multiple styles at once", () => {

		assert.equal(perfume("Hello (World):red:bold"), "Hello \u001b[1m\u001b[31mWorld\u001b[39m\u001b[22m");

	});

	it("should apply style to the whole string", () => {

		assert.equal(perfume("Hello (World):red", ["bold", "bgYellow"]), "\u001b[43m\u001b[1mHello \u001b[31mWorld\u001b[39m\u001b[22m\u001b[49m");

	});

	it("should parse emojis", () => {

		assert.equal(perfume("Hello (:smile:)"), "Hello ðŸ˜„");

	});

});
